  `description` varchar(255) DEFAULT NULL COMMENT '素材描述',
  `material_size` varchar(20) DEFAULT NULL COMMENT '素材大小',
  `save_path` varchar(255) DEFAULT NULL COMMENT '保存路径',
  `thumbnail` longtext COMMENT '缩略图',
  `audit_state` varchar(20) DEFAULT '0' COMMENT '审核状态 0待审批2未通过1通过',
  `audit_reason` varchar(255) DEFAULT NULL COMMENT '审核意见',
